// /src/App.js
import React, { useState } from 'react'
import styled from 'styled-components'
import JSZip from 'jszip'
import { saveAs } from 'file-saver'

const App = () => {
  const [files, setFiles] = useState([])
  const [borderColor, setBorderColor] = useState('#000000')
  const [borderWidth, setBorderWidth] = useState(5)

  const handleFileChange = (event) => {
    const uploadedFiles = Array.from(event.target.files)
    setFiles(uploadedFiles)
  }

  const handleBorderColorChange = (e) => {
    setBorderColor(e.target.value)
  }

  const handleBorderWidthChange = (e) => {
    setBorderWidth(Number(e.target.value))
  }

  const handleDownloadZip = async () => {
    const zip = new JSZip()

    for (const file of files) {
      if (file.type.startsWith('image')) {
        const canvas = document.createElement('canvas')
        const context = canvas.getContext('2d')
        const image = new Image()
        image.src = URL.createObjectURL(file)

        await new Promise((resolve) => {
          image.onload = () => {
            canvas.width = image.width + 2 * borderWidth
            canvas.height = image.height + 2 * borderWidth
            context.fillStyle = borderColor
            context.fillRect(0, 0, canvas.width, canvas.height)
            context.drawImage(image, borderWidth, borderWidth)
            canvas.toBlob((blob) => {
              zip.file(file.name, blob)
              resolve()
            })
          }
        })
      } else if (file.type.startsWith('video')) {
        const video = document.createElement('video')
        video.src = URL.createObjectURL(file)
        video.controls = true

        const canvas = document.createElement('canvas')
        const context = canvas.getContext('2d')
        video.addEventListener(
          'loadeddata',
          () => {
            canvas.width = video.videoWidth + 2 * borderWidth
            canvas.height = video.videoHeight + 2 * borderWidth
            context.fillStyle = borderColor
            context.fillRect(0, 0, canvas.width, canvas.height)
            context.drawImage(video, borderWidth, borderWidth, video.videoWidth, video.videoHeight)
            canvas.toBlob((blob) => {
              zip.file(`${file.name}_thumbnail.png`, blob)
              zip.file(file.name, file)
              // eslint-disable-next-line no-undef
              resolve()
            })
          },
          false
        )
      }
    }

    zip.generateAsync({ type: 'blob' }).then((content) => {
      saveAs(content, 'files.zip')
    })
  }

  return (
    <Container>
      <Title>Border Tool for Images & Videos</Title>

      <InputSection>
        <label htmlFor='file-upload'>Upload Images/Videos:</label>
        <input type='file' id='file-upload' onChange={handleFileChange} multiple accept='image/*,video/*' />

        <label htmlFor='border-color'>Border Color:</label>
        <input type='color' id='border-color' value={borderColor} onChange={handleBorderColorChange} />

        <label htmlFor='border-width'>Border Width (px):</label>
        <input type='number' id='border-width' value={borderWidth} onChange={handleBorderWidthChange} />
      </InputSection>

      <PreviewSection>
        {files.length > 0 &&
          files.map((file, index) => {
            if (file.type.startsWith('image')) {
              return (
                <ImagePreview key={index}>
                  <img
                    src={URL.createObjectURL(file)}
                    alt={file.name}
                    style={{
                      border: `${borderWidth}px solid ${borderColor}`,
                    }}
                  />
                </ImagePreview>
              )
            } else if (file.type.startsWith('video')) {
              return (
                <VideoPreview key={index}>
                  <video
                    src={URL.createObjectURL(file)}
                    controls
                    style={{
                      border: `${borderWidth}px solid ${borderColor}`,
                    }}></video>
                </VideoPreview>
              )
            } else {
              return null
            }
          })}
      </PreviewSection>

      <DownloadButton onClick={handleDownloadZip}>Download All as ZIP</DownloadButton>
    </Container>
  )
}

export default App

// Styled components for beautiful UI
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  font-family: Arial, sans-serif;
`

const Title = styled.h1`
  font-size: 2em;
  margin-bottom: 20px;
`

const InputSection = styled.div`
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
  gap: 10px;

  input {
    padding: 5px;
    font-size: 1em;
  }
`

const PreviewSection = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`

const ImagePreview = styled.div`
  img {
    max-width: 200px;
    max-height: 200px;
  }
`

const VideoPreview = styled.div`
  video {
    max-width: 200px;
    max-height: 200px;
  }
`

const DownloadButton = styled.button`
  padding: 10px 20px;
  font-size: 1.2em;
  background-color: #007bff;
  color: white;
  border: none;
  cursor: pointer;
  border-radius: 5px;

  &:hover {
    background-color: #0056b3;
  }
`
