import React, { useRef, useState } from 'react'
import { saveAs } from 'file-saver'

const VideoBorderTool = () => {
  const videoRef = useRef(null)
  const canvasRef = useRef(null)
  const [videoFile, setVideoFile] = useState(null)

  const handleFileChange = (event) => {
    const file = event.target.files[0]
    if (file && file.type.startsWith('video')) {
      setVideoFile(URL.createObjectURL(file))
    }
  }

  const addBorderToVideo = () => {
    const video = videoRef.current
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    video.play()

    function drawFrame() {
      if (!video.paused && !video.ended) {
        ctx.fillStyle = '#000' // Màu của border
        ctx.fillRect(0, 0, canvas.width, canvas.height)
        ctx.drawImage(video, 10, 10, canvas.width - 20, canvas.height - 20) // Vẽ video với border
        requestAnimationFrame(drawFrame)
      }
    }

    video.addEventListener('play', drawFrame)
  }

  return (
    <div>
      <input type='file' onChange={handleFileChange} accept='video/*' />
      {videoFile && (
        <>
          <video ref={videoRef} src={videoFile} style={{ display: 'none' }} />
          <canvas ref={canvasRef} width='640' height='480'></canvas>
          <button onClick={addBorderToVideo}>Add Border and Play</button>
        </>
      )}
    </div>
  )
}

export default VideoBorderTool
